import "./home.css"
import{Link} from 'react-router-dom'
import { useState, useEffect } from "react";
import { API } from "./config/api";

function Card(item) {

    return ( 
        <div id="group" key={item.id}>
            
        < img src ={require(`${item.image}`).default} className="image" alt = "" /><br/>
        <Link to = {`./detail-tour/${item.id}`}>
        <h2 >{item.name}</h2>
        </Link>
        
        <section>
        <p>{item.tag}</p><label>{item.country}</label>
        </section>
      
        </div>
    )
}
export default Card
