import './navbar.css'
import  { Modal }  from  'react-bootstrap' ;
import  { Button }  from  'react-bootstrap' ;
import  { Form ,Dropdown}  from  'react-bootstrap' ;
import React, { useState, useEffect } from 'react';
import {Link} from 'react-router-dom'
import { Redirect } from 'react-router-dom/cjs/react-router-dom.min';





function Navbar() {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [showRegister, setShowRegister] = useState(false);
    const handleCloseRegister = () => setShowRegister(false);
    const handleShowRegister = () => setShowRegister(true);

    
    const [form, setform] = useState({
      isLogin:false,user:{
        name:"",
        email:"",
        password:"",
        phone:"",
      }}
    )
    const handleRegister =(e) =>{
      setform((prevState) => ({
        ...prevState,
        [e.target.name]:e.target.value,
      }))
    }
    
    const handleSubmit =(e)=>{
      e.preventDefault();
      console.log(form)
      localStorage.setItem("register", JSON.stringify(form));
      
    }
    const data= JSON.parse(localStorage.getItem("status"));
    console.log(data)
    const [redirect, setredirect] = useState(data);
    const [login, setLogin] = useState({
      isLogin:false,user:{
        email:"",
        password:"",
      }
    }
    )
    const handleLogin =(e) =>{
      setLogin((prevState) => ({
        ...prevState,
        [e.target.name]:e.target.value,
      }))
    }
    const handleSubmitLogin =(e)=>{
      e.preventDefault();
      
      if(login.user.email == form.user.email){
    
        setform({
          isLogin:true,
        })
        setredirect(true);
        localStorage.setItem("status", JSON.stringify(true));
      
      }else{

        setform({
          isLogin:false,
        })
        setredirect(false);
        localStorage.setItem("status", JSON.stringify(false))
              }
      console.log(login)
      
    }
    
    if(redirect){
      return <NavbarLogin/>
      
    }
    const email=JSON.parse(localStorage.getItem("register"));
    console.log(email.user.email)
    if(email.user.email =="admin@admin.com"){
      localStorage.setItem("admin", JSON.stringify(true))
    }
 
  
    return ( 
        <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" > 
        <button id = "login" onClick={handleShow}> Login </button> 
        <button id = "register" onClick= {handleShowRegister}> Register </button>  
        </div >

        <div id='modal'> 
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmitLogin}>
            <label>Email</label><br/>
            <input id="emailLogin" name="email" type='email' onChange={handleLogin} required></input><br/>
            <label>Password</label><br/>
            <input id="passwordLogin" type='password' name="password" onChange={handleLogin} required></input><br/>
            <button id="submit" type="submit">Submit</button>
            </form>
            
        </Modal.Body>
        </div>
        <Modal.Footer>
        <div id="btn-register">
        
        
        </div>
        </Modal.Footer>
    </Modal>

  
    </div>
    <div id="modal-register">
    <Modal show={showRegister} onHide={handleCloseRegister}>
        <Modal.Header closeButton>
          <Modal.Title>Register</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmit}>
            <label>Full Name</label><br/>
            <input name="name" type='text' onChange={handleRegister}/><br/>
            <label>Email</label><br/>
            <input name="email" type='email' onChange={handleRegister}></input><br/>
            <label>Password</label><br/>
            <input name="password" type='password' onChange={handleRegister}></input><br/>
            <label>Phone</label><br/>
            <input name="phone" type='number' onChange={handleRegister}></input><br/>
            <button id="submit" type="submit">Submit</button>
            </form>
            
        </Modal.Body>
        </div>
        <Modal.Footer>
        <div id="btn-register">
        
        
        </div>
        </Modal.Footer>
    </Modal>
    </div>
    
    </div>
    );
}

function NavbarLogin(){
  
  const logout=()=>{
    localStorage.setItem("status", JSON.stringify(false));
    return(<Redirect to="/"/>)
  }
  return(
  <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" >
        <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
        <img id="user-img" src={require("./assets/user (1).png").default} alt="user"/>
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item ><Link to="/profile">Profile</Link></Dropdown.Item>
          <Dropdown.Item ><Link to="/payment">Pay</Link></Dropdown.Item>
          <Dropdown.Item  onClick={logout}>Logout</Dropdown.Item>
        </Dropdown.Menu>
</Dropdown>
            
        </div >
  </div>
  )
}


export default Navbar
