import './navbar.css'
import  { Modal }  from  'react-bootstrap' ;
import  { Button }  from  'react-bootstrap' ;
import  { Form ,Dropdown}  from  'react-bootstrap' ;
import React, { useState } from 'react';


function Navbar() {
    const [form, setform] =useState({
      name:'',
      email:'',
      password:'',
      phone:''
    })
    const handleRegister =(e) =>{
      setform((prevState) => ({
        ...prevState,
        [e.target.name]:e.target.value,
      }))
    }

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [showRegister, setShowRegister] = useState(false);
    const handleCloseRegister = () => setShowRegister(false);
    const handleShowRegister = () => setShowRegister(true);

    const[emailLogin,setEmailLogin] = useState('');
    const[passwordLogin,setPasswordLogin] = useState('');
   
      const[name,setName] = useState('');
      const[email,setEmail] = useState('');
      const[password,setPassword] = useState('');
      const[phone,setPhone] = useState('');
   
      const [redirect, setRedirect]=useState(false);

    const handleSubmit =(e)=>{
      e.preventDefault();
      console.log(form)
      
    }
    if(redirect){
      return <NavbarLogin/>
    }

    const handleSubmitLogin =(e)=>{
      e.preventDefault();
      console.log({
        emailLogin,
        passwordLogin,
      })
    }
  
    return ( 
        <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" > 
        <button id = "login" onClick={handleShow}> Login </button> 
        <button id = "register" onClick= {handleShowRegister}> Register </button>  
        </div >

        <div id='modal'> 
        <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false} >
        <Modal.Header closeButton>
            <div id="modal-tittle">
            <Modal.Title>Login</Modal.Title>
            </div>
        </Modal.Header>
        <Modal.Body>
    <Form onSubmit={handleSubmitLogin}>
    <Form.Group className="mb-3" controlId="formBasicEmail">
    
    <div id="label">
    <Form.Label>Email</Form.Label>
    </div>
    <div id="input">
    <Form.Control type="email" placeholder="Enter email" onChange={e=>setEmailLogin(e.target.value)}/>
    <Form.Text className="text-muted">
    </Form.Text>
    </div>
    </Form.Group>

    <Form.Group className="mb-3" controlId="formBasicPassword">
    <div id="label">
    <Form.Label>Password</Form.Label>
    </div>
    <div id="input">
    <Form.Control type="password" placeholder="Password" onChange={e=>setPasswordLogin(e.target.value)} />
    </div>
  </Form.Group>

  <Button variant="primary" type="submit">
    Login
  </Button><br/>
  <p>Don't have an account? ? <a>Klik Here</a></p>
</Form>
    </Modal.Body>
    </Modal>
    </div>
    <div id="modal-register">
    <Modal show={showRegister} onHide={handleCloseRegister}>
        <Modal.Header closeButton>
          <Modal.Title>Register</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmit}>
            <label>Full Name</label><br/>
            <input name="name" onChange={handleRegister}/><br/>
            <label>Email</label><br/>
            <input name="email" onChange={handleRegister}></input><br/>
            <label>Password</label><br/>
            <input name="password" onChange={handleRegister}></input><br/>
            <label>Phone</label><br/>
            <input name="phone" onChange={handleRegister}></input><br/>
            <button id="submit" type="submit">Submit</button>
            </form>
            
        </Modal.Body>
        </div>
        <Modal.Footer>
        <div id="btn-register">
        
        
        </div>
        </Modal.Footer>
    </Modal>
    </div>
    
    </div>
    );
}

function NavbarLogin(){
  return(
  <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" >
        <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
        <img id="user-img" src={require("./assets/user (1).png").default} alt="user"/>
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item href="#/action-1">Profile</Dropdown.Item>
          <Dropdown.Item href="#/action-2">Pay</Dropdown.Item>
          <Dropdown.Item href="#/action-3">Logout</Dropdown.Item>
        </Dropdown.Menu>
</Dropdown>
            
        </div >
  </div>
  )
}


export default Navbar
