import React from "react";
import {Form, Button, Row, Col, FloatingLabel, Alert} from "react-bootstrap"
import './add-trip.css'
import NavbarLogin from "./components/navbarLogin";
import Footer from "./components/footer";
import { useState, useEffect } from "react";
import { API, setAuthToken } from "./config/api"
import { useHistory } from "react-router-dom";

function Add(){
  let history = useHistory()

  const [country, setCountry] = useState([]); //Store all category data
  const [idCountry, setIdCountry] = useState([]); //Save the selected category id
  const [preview, setPreview] = useState(null); //For image preview

  const getCountry = async () => {
    try {
      const response = await API.get("/countries");
      setCountry(response.data.data);
    } catch (error) {
      console.log(error);
    }
  };
  const handleChangeIdCountry = (e) => {
    const id = e.target.value;
    const checked = e.target.checked;

    if (checked) {
      // Save category id if checked
      setIdCountry([...idCountry, parseInt(id)]);
    } else {
      // Delete category id from variable if unchecked
      let newIdCountry = idCountry.filter((idCountryItem) => {
        return idCountryItem != id;
      });
      setIdCountry(newIdCountry);
    }
  };


  const [trip, setTrip] = useState({
    tittle:"",
    accomodation:"",
    transportation:"",
    eat:"",
    day:"",
    night:"",
    dateTrip:"",
    price:0,
    quota:0,
    description:"",
    image:"",
  }
)
const handleOnChange = (e) => {
  setTrip({
    ...trip,
    [e.target.name]: e.target.type === "file" ? e.target.files : e.target.value,
  });

  // Create image url for preview
  if (e.target.type === "file") {
    let url = URL.createObjectURL(e.target.files[0]);
    setPreview(url);
  }
};
const handleSubmit = async (e) => {
  try {
    e.preventDefault();

    // Create Configuration Content-type here ...
    // Content-type: multipart/form-data
    const config = {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    };

    // Create store data with FormData as object here ...
    const formData = new FormData();
    formData.set("tittle", trip.tittle);
    formData.set("categoryId", idCountry);
    formData.set("accomodation", trip.accomodation);
    formData.set("transportation", trip.transportation);
    formData.set("eat", trip.eat);
    formData.set("day", trip.day);
    formData.set("night", trip.night);
    formData.set("dateTrip", trip.dateTrip);
    formData.set("price", trip.price);
    formData.set("quota", trip.quota);
    formData.set("description", trip.description);
    formData.set("image", trip.image[0], trip.image[0].name);

    // Insert product data here ...
    const response = await API.post("/trip", formData, config);
    console.log(response);
    history.push("/detail-tour");
  } catch (error) {
    console.log(error);
  }
};

useEffect(() => {
  getCountry();
}, []);

    return(
      <>
      <NavbarLogin/>
       <div id="con">
  <h1>Add Trip</h1>
  <Form onSubmit={handleSubmit}>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Tittle Trip</Form.Label>
<Form.Control onChange={handleOnChange} name="tittle" type="text" />
</Form.Group>

{country.map((item, index) => (
  <label key={index} className="dropdown-inline me-4">
  <input type="checkbox" value={item.id} onClick={handleChangeIdCountry} /> {item.name}
  </label>
))}
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Accomodation</Form.Label>
<Form.Control onChange={handleOnChange} name="accomodation" type="text" />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Transportation</Form.Label>
<Form.Control onChange={handleOnChange} name="transportation" type="text" />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Eat</Form.Label>
<Form.Control onChange={handleOnChange} name="eat" type="text" />
</Form.Group>
<Row>
<Col>
<Form.Label  >Day</Form.Label>
<Form.Control onChange={handleOnChange} name="day"  />
</Col>
<Col>
<Form.Label>Night</Form.Label>
<Form.Control onChange={handleOnChange} name="night" />
</Col>
</Row>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Date Trip</Form.Label>
<Form.Control onChange={handleOnChange} name="dateTrip" type="text" />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Quota</Form.Label>
<Form.Control onChange={handleOnChange} name="quota" type="text"  />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Price</Form.Label>
<Form.Control onChange={handleOnChange} name="price" type="text"  />
</Form.Group>
<Form.Label>Descriptions</Form.Label>
<FloatingLabel controlId="floatingTextarea2" label="Comments">
<Form.Control onChange={handleOnChange}
name="descriptions"
as="textarea"
placeholder="Leave a comment here"
style={{ height: '100px' }}
/>
</FloatingLabel>
{preview && (
<div>
<img src={preview}
    style={{
    maxWidth: "150px",
    maxHeight: "150px",
    objectFit: "cover",
    }}
    alt="preview"/>
</div>
)}
<input type="file" id="upload" name="image" hidden onChange={handleOnChange} />
<label for="upload" className="label-file-add-product">Upload file</label>

<Button variant="warning" type="submit">
Add Trip
</Button>
</Form>

</div>
<Footer/>
      </>
     
     
    
    )
}
export default Add
