import "./home.css"
import{Link} from 'react-router-dom'
import { useHistory } from "react-router-dom/cjs/react-router-dom.min"
import { useState,useEffect } from "react";
import { API } from "./config/api";

function Card({item}) {
    const [country, setCountry] = useState([]);
    const idCountry= JSON.parse(sessionStorage.getItem('idCountry'));
      // Create function get products data from database here ...
      const getCountry = async (e) => {
        try {
        
          const response = await API.get(`/country/${item.idCountry}`);
          setCountry(response.data.data);
          console.log(response)
        } catch (error) {
          console.log(error);
        }
      };
    
      useEffect(() => {
        getCountry();
        
      }, []);
    let history =useHistory()
    const convrt =JSON.parse(item.image)
  const detail=()=>{
    history.push(`./detail-tour/${item.id}`)
    sessionStorage.setItem('idCountry', JSON.stringify(item.idCountry));
    sessionStorage.setItem('img1', JSON.stringify(convrt[0]));
    sessionStorage.setItem('img1', JSON.stringify(convrt[1]));
    sessionStorage.setItem('img1', JSON.stringify(convrt[2]));
    sessionStorage.setItem('img1', JSON.stringify(convrt[3]));
    sessionStorage.setItem('img1', JSON.stringify(convrt[4]));
  }

  
    return ( 
        <div id="group" key={item.id}>
            <p id="quota">/{item.quota}</p>
        < img id="gbr" onClick={detail} src ={require(`../../server/uploads/${convrt[0]}`).default} className="image" alt = "" /><br/>
        <h2 onClick={detail} >{item.tittle}</h2>
        
        <section>
        <p>Rp.{item.price}</p><label>{country.name}</label>
        </section>
      
        </div>
    )
}
export default Card
