import "./home.css"
import{Link} from 'react-router-dom'
import { useState, useEffect } from "react";
import { API } from "./config/api";

function Card(item) {
    const [trips, setTrips] = useState([]);

    // Create function get products data from database here ...
    const getTrips = async () => {
      try {
        const response = await API.get("/trips");
        setTrips(response.data.data);
      } catch (error) {
        console.log(error);
      }
    };
    // Call function get products with useEffect didMount here ...
    useEffect(() => {
      getTrips();
    }, []);
    return ( 
        <div id="group" key={item.id}>
            
        < img src ={require(`${item.image}`).default} className="image" alt = "" /><br/>
        <Link to = {`./detail-tour/${item.id}`}>
        <h2 >{item.name}</h2>
        </Link>
        
        <section>
        <p>{item.tag}</p><label>{item.country}</label>
        </section>
      
        </div>
    )
}
export default Card
