import Navbar from './components/navbar'
import React from "react";
import "./payment.css"
import Footer from './components/footer'
import NavbarLogin from './components/navbarLogin';
import { useParams } from 'react-router-dom';
import { useState,useEffect } from 'react';
import { API } from './config/api';


function Payment(){
    const idUser= JSON.parse(sessionStorage.getItem('user'));
    const idTrip= JSON.parse(sessionStorage.getItem('idTrip'));
    const [transaction, setTransaction] = useState([]);
    const [user, setUser] = useState([]);
    const [trip, setTrip] = useState([]);
    
    // Create function get products data from database here ...
    const getUser = async () => {
      try {
        const response = await API.get(`/user/${idUser}`);
        setUser(response.data.data);
        console.log(response)
       
      } catch (error) {
        console.log(error);
      }
    };
    useEffect(() => {
        getUser();
      }, []);
    
      
    
      // Create function get products data from database here ...
      const getTransaction = async () => {
        try {
          const response = await API.get(`/user/${idUser}`);
          setTransaction(response.data.newdata);
        
         
        } catch (error) {
          console.log(error);
        }
      };
      useEffect(() => {
          getTransaction();
        }, []);
        const getTrip = async () => {
            try {
              const response = await API.get(`/trip/${idTrip}`);
              setTrip(response.data.data);
              console.log(response)
             
            } catch (error) {
              console.log(error);
            }
          };
         
          useEffect(() => {
              getTrip();
            }, []);
            console.log(user)
            console.log(transaction)
            console.log(trip)
    return(
        <>
        <NavbarLogin/>
        <div id="pay-container">
            
            <div id="card">
            <div id="pay-top">
                <img src={require("./components/assets/Icon.png").default} alt="icon"/>
                <div id="booking">
                    <h1>Booking</h1>
                    <p></p>
                </div>
            </div>
            <div id="main-con">
                <div id="main-pay">
                    <div id="pay-name">
                    <h1>{trip.tittle}</h1 >
                    <p></p>
                    </div>
                    <div id="date">
                        <h3>Date Trip</h3>
                        <p>{trip.dateTrip}</p>
                    </div>
                    <div id="duration">
                        <h3>Duration</h3>
                        <p>{trip.day} Day {trip.night} Night</p>
                    </div>
                    <div id="status">
                        <span>{transaction.status}</span>
                    </div>
                    <div>
                        <h3>Accomodation</h3>
                        <p id="accomodation">{trip.accomodation}</p>
                    </div>
                    <div>
                        <h3 >Transporartion</h3>
                        <p id="transporartion">{trip.transportation}</p>
                    </div>
                </div>
                <div id="photo">
                    <img src= {require("./components/assets/2204979_20170708045545 1.png").default} alt="photo"/>
                    <p>upload payment proof</p>
                </div>
            </div>
            <div id="tabel-con">
            <table class="table">
  <thead>
    <tr>
      <th scope="col">No</th>
      <th scope="col">Full Name</th>
      <th scope="col">Phone</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>{user.fullName}</td>
      <td>{user.phone}</td>
      <td></td>
    </tr>
  </tbody>
</table>
<div id= "qty">
    <span>Qty</span><span>:</span><span>{transaction.counterQty}</span><br/>
    <span>Total</span><span>:</span><span>{transaction.total}</span>
</div>
            </div>
           
            </div>
            <button id="btn-pay">Pay</button>
            

        </div>
        <Footer/>
        </>
    )
}
export default Payment
