import './navbar.css'
import  { Modal }  from  'react-bootstrap' ;
import  {Dropdown}  from  'react-bootstrap' ;
import React, { useState, useHistory,useContext } from 'react';
import {Link} from 'react-router-dom'
import { Redirect } from 'react-router-dom/cjs/react-router-dom.min';
import User from './data-user.json'
import { Alert } from "react-bootstrap";
import { UserContext } from "../context/userContext";
import { API, setAuthToken } from "../config/api";
S

function Navbar() {
    const history = useHistory();

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [showRegister, setShowRegister] = useState(false);
    const handleCloseRegister = () => setShowRegister(false);
    const handleShowRegister = () => setShowRegister(true);

    const [state, dispatch] = useContext(UserContext);
    
  const [message, setMessage] = useState(null);

    const [form, setform] = useState({
      isLogin:false,user:{
        name:"",
        email:"",
        password:"",
        phone:"",
      }}
    )
    const handleRegister =(e) =>{
      setform((prevState) => ({
        ...prevState,
        [e.target.name]:e.target.value,
      }))
    }
    
    const handleSubmit =async (e)=>{
      try{
      e.preventDefault();
      console.log(form)
      const config={
        headers:{
          "Content-Type":"application/json"
        }
      }
      const body = JSON.stringify(form)
      const response = await API.post("/Navbar",body,config)
      if (response.data.status === "success...") {
        const alert = (
          <Alert variant="success" className="py-1">
            Success
          </Alert>
        );
        setMessage(alert);
      } else {
        const alert = (
          <Alert variant="danger" className="py-1">
            Failed
          </Alert>
        );
        setMessage(alert);
      }
    } catch (error) {
      const alert = (
        <Alert variant="danger" className="py-1">
          Failed
        </Alert>
      );
      setMessage(alert);
      console.log(error);
    }
  };

    const [login, setLogin] = useState({
      isLogin:false,user:{
        email:"",
        password:"",
      }
    }
    )
    const handleLogin =(e) =>{
      setLogin((prevState) => ({
        ...prevState,
        [e.target.name]:e.target.value,
      }))
    }
    
   console.log(login.email)
    const handleSubmitLogin =async (e)=>{
      try {
      e.preventDefault();
      if(login.email == User[1].email){
        sessionStorage.setItem("isLogin", JSON.stringify(true));
        sessionStorage.setItem("Admin", JSON.stringify(true));
      }else if(login.email == User[0].email){
        sessionStorage.setItem("isLogin", JSON.stringify(true));
      }
      else{
        sessionStorage.setItem("isLogin", JSON.stringify(false));
        sessionStorage.setItem("Admin", JSON.stringify(false));
      }
   
  
        // Create Configuration Content-type here ...
        // Content-type: application/json
        const config = {
          headers: {
            "Content-Type": "application/json",
          },
        };
  
        // Convert form data to string here ...
        const body = JSON.stringify(form);
  
        // Insert data user for login process ...
        const response = await API.post("/navbarLogin", body, config);
        setAuthToken(response.data.data.token);
        // Checking process
        if (response?.status === 200) {
          // Send data to useContext
          dispatch({
            type: "LOGIN_SUCCESS",
            payload: response.data.data,
          });
  
          // Status check
          if (response.data.data.status === "admin") {
            history.push("/complain-admin");
          } else {
            history.push("/");
          }
  
          const alert = (
            <Alert variant="success" className="py-1">
              Login success
            </Alert>
          );
          setMessage(alert);
        }
      } catch (error) {
        const alert = (
          <Alert variant="danger" className="py-1">
            Login failed
          </Alert>
        );
        setMessage(alert);
        console.log(error);
      }
    }
    const admin= JSON.parse(sessionStorage.getItem("Admin"));
    const isLogin= JSON.parse(sessionStorage.getItem("isLogin"));
    if(admin==false && isLogin==true){
      return(<NavbarLogin/>)
    }
 
  
    return ( 
        <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" > 
        <button id = "login" onClick={handleShow}> Login </button> 
        <button id = "register" onClick= {handleShowRegister}> Register </button>  
        </div >

        <div id='modal'> 
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmitLogin}>
            <label>Email</label><br/>
            <input id="emailLogin" name="email" type='email' onChange={handleLogin} required></input><br/>
            <label>Password</label><br/>
            <input id="passwordLogin" type='password' name="password" onChange={handleLogin} required></input><br/>
            <div id="btn-login">
            <button id="handlelogin" type="submit">Login</button>
            </div>
            </form>
            
        </Modal.Body>
        </div>
    </Modal>

  
    </div>
    <div id="modal-register">
    <Modal show={showRegister} onHide={handleCloseRegister}>
        <Modal.Header closeButton>
          <Modal.Title>Register</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmit}>
            <label>Full Name</label><br/>
            <input name="name" type='text' onChange={handleRegister}/><br/>
            <label>Email</label><br/>
            <input name="email" type='email' onChange={handleRegister}></input><br/>
            <label>Password</label><br/>
            <input name="password" type='password' onChange={handleRegister}></input><br/>
            <label>Phone</label><br/>
            <input name="phone" type='number' onChange={handleRegister}></input><br/>
            <button id="btn-regis"  type="submit">Register</button>
            </form>
            
        </Modal.Body>
        </div>
        <Modal.Footer>
        <div id="btn-register">
        
        
        </div>
        </Modal.Footer>
    </Modal>
    </div>
    
    </div>
    );
}

function NavbarLogin(){
  
  const logout=()=>{
    localStorage.setItem("status", JSON.stringify(false));
    return(<Redirect to="/"/>)
  }
  return(
  <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" >
        <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
        <img id="user-img" src={require("./assets/user (1).png").default} alt="user"/>
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item ><Link to="/profile">Profile</Link></Dropdown.Item>
          <Dropdown.Item ><Link to="/payment">Pay</Link></Dropdown.Item>
          <Dropdown.Item  onClick={logout}>Logout</Dropdown.Item>
        </Dropdown.Menu>
</Dropdown>
            
        </div >
  </div>
  )
}


export default Navbar
