import React from "react";
import {Form, Button, Row, Col, FloatingLabel, Alert} from "react-bootstrap"
import './add-trip.css'
import NavbarLogin from "./components/navbarLogin";
import Footer from "./components/footer";
import { useState } from "react";
import { API, setAuthToken } from "./config/api"

function Add(){

  const [message, setMessage] = useState(null);

  const [trip, settrip] = useState({
    tittle:"",
    accomodation:"",
    transportation:"",
    eat:"",
    day:"",
    night:"",
    dateTrip:"",
    price:"",
    quota:"",
    description:"",
    image:"",
  }
)
const handleOnChange = (e) => {
  settrip({
    [e.target.name]: e.target.value,
  });
};
const handleSubmit = async (e)=>{
  try{
  e.preventDefault();

  const body = JSON.stringify(trip)
  console.log(body)
  const response = await API.post("/trip",body)
  console.log(response)
  if (response.data.status == "success...") {
    const alert = (
      <Alert variant="success" className="py-1">
        Success
      </Alert>
    );
    setMessage(alert);
    settrip({
      tittle:"",
      accomodation:"",
      transportation:"",
      eat:"",
      day:"",
      night:"",
      dateTrip:"",
      price:"",
      quota:"",
      description:"",
      image:"",
    });
  } else {
    const alert = (
      <Alert variant="danger" className="py-1">
        Failed
      </Alert>
    );
    setMessage(alert);
  }
} catch (error) {
  const alert = (
    <Alert variant="danger" className="py-1">
      Failed
    </Alert>
  );
  setMessage(alert);
  console.log(error);
}
};
    return(
      <>
      <NavbarLogin/>
       <div id="con">
  <h1>Add Trip</h1>
  <Form onSubmit={handleSubmit}>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Tittle Trip</Form.Label>
<Form.Control onChange={handleOnChange} name="tittle" type="text" />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Accomodation</Form.Label>
<Form.Control onChange={handleOnChange} name="accomodation" type="text" />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Transportation</Form.Label>
<Form.Control onChange={handleOnChange} name="transportation" type="text" />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Eat</Form.Label>
<Form.Control onChange={handleOnChange} name="eat" type="text" />
</Form.Group>
<Row>
<Col>
<Form.Label  >Day</Form.Label>
<Form.Control onChange={handleOnChange} name="day"  />
</Col>
<Col>
<Form.Label>Night</Form.Label>
<Form.Control onChange={handleOnChange} name="night" />
</Col>
</Row>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Date Trip</Form.Label>
<Form.Control onChange={handleOnChange} name="dateTrip" type="text" />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Quota</Form.Label>
<Form.Control onChange={handleOnChange} name="quota" type="text"  />
</Form.Group>
<Form.Group className="mb-3" controlId="formBasicEmail">
<Form.Label>Price</Form.Label>
<Form.Control onChange={handleOnChange} name="price" type="text"  />
</Form.Group>
<Form.Label>Descriptions</Form.Label>
<FloatingLabel controlId="floatingTextarea2" label="Comments">
<Form.Control onChange={handleOnChange}
name="descriptions"
as="textarea"
placeholder="Leave a comment here"
style={{ height: '100px' }}
/>
</FloatingLabel>
<Form.Group className="position-relative mb-3">
            <Form.Label>File</Form.Label>
            <Form.Control
              type="file"
    
              name="image"
            />
          </Form.Group>

<Button variant="warning" type="submit">
Add Trip
</Button>
</Form>

</div>
<Footer/>
      </>
     
     
    
    )
}
export default Add
