import Footer from "./footer"
import NavbarLogin from "./navbarLogin"
import './profile.css'
import { useState,useEffect } from "react"
import {API} from'../config/api'

function Profile(){
    const id= JSON.parse(sessionStorage.getItem('user'));
    const [profile, setProfile] = useState({});
    const [transaction, settransaction] = useState({});
    const [trip, settrip] = useState({});
    
    const getProfile = async () => {
        try {
        const response = await API.get(`/user/${id}`);
          // Store product data to useState variabel
        setProfile(response.data.data);
        } catch (error) {
        console.log(error);
        }
    };
    useEffect(() => {
        getProfile();
      }, []);

      const getTransaction = async () => {
        try {
        const response = await API.get(`/user/${id}`);
          // Store product data to useState variabel
        settransaction(response.data.data.transactions);
        } catch (error) {
        console.log(error);
        }
    };
    useEffect(() => {
        getTransaction();
      }, []);

      const gettrip = async () => {
        try {
        const response = await API.get(`/trip/${transaction.idTrip}`);
          // Store product data to useState variabel
        settrip(response.data.data);
        } catch (error) {
        console.log(error);
        }
    };

    useEffect(() => {
        gettrip();
      }, []);
  console.log(trip)

      if(transaction.status=="approve" ||transaction.status=="Canceled"  ){
          return(
            <>
        <NavbarLogin/>
        <div id="profile-container">
            <div id="left-profile">
                <h1>Personal Info</h1>
                <label>{profile.fullName}</label>
                <p>Email</p>
                <label>{profile.email}</label>
                <p>Phone</p>
                <label>{profile.phone}</label>
                <p>Address</p>
                <label>{profile.address}</label>
            </div>
            <div id="right-profile">
                <img src={require("./assets/profile.png").default}></img><br/>
                <button>Change Photo Profil</button>
    
            </div>
        </div>
        <tr>
        <th scope="row">1</th>
        <td>{profile.fullName}</td>
        <td>{trip.tittle}</td>
        <td>{transaction.status}</td>
        <td>{}</td>
        </tr>
        
        <Footer/>
        </>
    )
            
      }else{
    return(
        <>
        <NavbarLogin/>
        <div id="profile-container">
            <div id="left-profile">
                <h1>Personal Info</h1>
                <label>{profile.fullName}</label>
                <p>Email</p>
                <label>{profile.email}</label>
                <p>Phone</p>
                <label>{profile.phone}</label>
                <p>Address</p>
                <label>{profile.address}</label>
            </div>
            <div id="right-profile">
                <img src={require("./assets/profile.png").default}></img><br/>
                <button>Change Photo Profil</button>
            </div>
        </div>
        <Footer/>
        </>
    )
      }
}

export default Profile
