import './navbar.css'
import  { Modal }  from  'react-bootstrap' ;
import  { Button }  from  'react-bootstrap' ;
import  { Form ,Dropdown}  from  'react-bootstrap' ;
import React, { useState, useEffect } from 'react';


function Navbar() {
  
    const [form, setform] = useState({
      isLogin:false,}
    )
    const handleRegister =(e) =>{
      setform((prevState) => ({
        ...prevState,
        [e.target.name]:e.target.value,
      }))
    }
    
    useEffect(()=>{
      console.log("mount");
      return()=>{
        console.log("unmount");
      }
    },[])

   

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [showRegister, setShowRegister] = useState(false);
    const handleCloseRegister = () => setShowRegister(false);
    const handleShowRegister = () => setShowRegister(true);

    
    
    const handleSubmit =(e)=>{
      e.preventDefault();
      console.log(form)
    }
    
    const [login, setLogin] = useState({
      isLogin:false,
    }
    )
    const handleLogin =(e) =>{
      setLogin((prevState) => ({
        ...prevState,
        [e.target.name]:e.target.value,
      }))
    }
   
    const handleSubmitLogin =(e)=>{
      e.preventDefault();
      if(login.email=== form.email && login.password === form.password){
        console.log("sama")
        setLogin({
          isLogin:true,
        })
        sessionStorage.setItem('isLogin', true);
      }else{
      console.log("beda")
      setLogin({
        isLogin:false,
      })
      sessionStorage.setItem('isLogin', false);
      }
      console.log(login)
      let data = sessionStorage.getItem('isLogin');
  
    }
 
      
    if(sessionStorage.getItem("isLogin")){
      return(<NavbarLogin/>)
    }else{
      return ( 
        <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" > 
        <button id = "login" onClick={handleShow}> Login </button> 
        <button id = "register" onClick= {handleShowRegister}> Register </button>  
        </div >

        <div id='modal'> 
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmitLogin}>
            <label>Email</label><br/>
            <input id="emailLogin" name="email" type='email' onChange={handleLogin} required></input><br/>
            <label>Password</label><br/>
            <input id="passwordLogin" type='password' name="password" onChange={handleLogin} required></input><br/>
            <button id="submit" type="submit">Submit</button>
            </form>
            
        </Modal.Body>
        </div>
        <Modal.Footer>
        <div id="btn-register">
        
        
        </div>
        </Modal.Footer>
    </Modal>

  
    </div>
    <div id="modal-register">
    <Modal show={showRegister} onHide={handleCloseRegister}>
        <Modal.Header closeButton>
          <Modal.Title>Register</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmit}>
            <label>Full Name</label><br/>
            <input name="name" type='text' onChange={handleRegister}/><br/>
            <label>Email</label><br/>
            <input name="email" type='email' onChange={handleRegister}></input><br/>
            <label>Password</label><br/>
            <input name="password" type='password' onChange={handleRegister}></input><br/>
            <label>Phone</label><br/>
            <input name="phone" type='number' onChange={handleRegister}></input><br/>
            <button id="submit" type="submit">Submit</button>
            </form>
            
        </Modal.Body>
        </div>
        <Modal.Footer>
        <div id="btn-register">
        
        
        </div>
        </Modal.Footer>
    </Modal>
    </div>
    
    </div>
    );
}
    }
    

function NavbarLogin(){
  return(
  <div class = "navbar" >
        <div id = "nav-right" >
        <img src = { require("./assets/Icon.png").default } alt = "Icon" />
        </div> 
        <div id = "nav-left" >
        <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
        <img id="user-img" src={require("./assets/user (1).png").default} alt="user"/>
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item href="#/action-1">Profile</Dropdown.Item>
          <Dropdown.Item href="#/action-2">Pay</Dropdown.Item>
          <Dropdown.Item href="#/action-3">Logout</Dropdown.Item>
        </Dropdown.Menu>
</Dropdown>
            
        </div >
  </div>
  )
}


export default Navbar
