import Navbar from './components/navbar'
import React from "react";
import "./payment.css"
import Footer from './components/footer'
import NavbarLogin from './components/navbarLogin';
import { useParams } from 'react-router-dom';
import { useState,useEffect } from 'react';
import { API } from './config/api';


function Payment(){
    const idUser= JSON.parse(sessionStorage.getItem('user'));
    const [user, setUser] = useState([]);
    
    // Create function get products data from database here ...
    const getUser = async () => {
      try {
        const response = await API.get(`/user/${idUser}`);
        setUser(response.data.data);
       
      } catch (error) {
        console.log(error);
      }
    };
    useEffect(() => {
        getUser();
      }, []);
    console.log(user.transactions)
    const [trip, setTrip] = useState([]);
  
    // Create function get products data from database here ...
    const getTrip = async () => {
      try {
        const response = await API.get(`/transaction/${user.idTransactions}`);
        setTrip(response.data.data);
       
      } catch (error) {
        console.log(error);
      }
    };
    useEffect(() => {
        getTrip();
      }, []);
      console.log(trip)
    return(
        <>
        <NavbarLogin/>
        <div id="pay-container">
            
            <div id="card">
            <div id="pay-top">
                <img src={require("./components/assets/Icon.png").default} alt="icon"/>
                <div id="booking">
                    <h1>Booking</h1>
                    <p>Saturday, 22 Juy 2020</p>
                </div>
            </div>
            <div id="main-con">
                <div id="main-pay">
                    <div id="pay-name">
                    <h1>6D/4N Fun Tassie Vacation</h1 >
                    <p>Australia</p>
                    </div>
                    <div id="date">
                        <h3>Date Trip</h3>
                        <p>26 August 2020</p>
                    </div>
                    <div id="duration">
                        <h3>Duration</h3>
                        <p>6 Day 4 Night</p>
                    </div>
                    <div id="status">
                        <span>Waiting Payment</span>
                    </div>
                    <div>
                        <h3>Accomodation</h3>
                        <p id="accomodation">Hotel 4 Nights</p>
                    </div>
                    <div>
                        <h3 >Transporartion</h3>
                        <p id="transporartion">Qatar Airways</p>
                    </div>
                </div>
                <div id="photo">
                    <img src= {require("./components/assets/2204979_20170708045545 1.png").default} alt="photo"/>
                    <p>upload payment proof</p>
                </div>
            </div>
            <div id="table">
                <label>No</label>
                <label>Full Name</label>
                <label>Gender</label>
                <label>Phone</label>
            </div>
            <div id="table-con">
                <div id="contain">

                </div>
                <div id="qty">

                </div>
            </div>
            
            </div>
            <button id="btn-pay">Pay</button>
            

        </div>
        <Footer/>
        </>
    )
}
export default Payment
