import Navbar from './components/navbar'
import React from "react";
import "./detail-tour.css"
import Footer from './components/footer'
import { useState,useEffect } from 'react';
import {Link}from 'react-router-dom'
import { useParams } from 'react-router-dom';
import { API } from './config/api';

function Tour(){
    const [number,setCount] = useState(0)
    const handleMin =()=>{
    setCount((prevState) => prevState - 1);
    }
    const handlePlus =()=>{
        setCount((prevState) => prevState + 1);
    }
    const total= number * 12398000;

    let { id } = useParams();

    const [trip, setTrip] = useState([]);

  // Create function get products data from database here ...
  const getProducts = async () => {
    try {
      const response = await API.get(`/trip/${id}`);
      setTrip(response.data.data);
    } catch (error) {
      console.log(error);
    }
  };
  // Call function get products with useEffect didMount here ...
  useEffect(() => {
    getProducts();
  }, []);
    return(
        <div id="tour-container">
            <Navbar/>
            <h1>6D/4N Fun Tassie Vacation + Sydney</h1>
            <p id="country">Australia</p>
            <div id="detail-img">
                <div id="main-img">
                    <img src={require("./components/assets/Screen Shot 2019-10-10 at 11.42 1.png").default} alt="main-img"/>
                </div>
                <div id="secondary-img">
                <img src="" alt="main-img"/>
                <img src="" alt="main-img"/>
                <img src=""alt="main-img"/>
                </div>
            </div>
            <div id="info">
            
            <h2>Information Trip</h2>
            <div id="dis-info">
                <div>
                    <p>Accommodation</p>
                    <img src={require("./components/assets/hotel 1.png").default} alt="detail-icon"/>
                    <h3>Hotel 4 Nights</h3>
                </div>
                <div>
                    <p>Transportation</p>
                    <img src={require("./components/assets/plane 1.png").default} alt="detail-icon"/>
                    <h3>Qatar Airways</h3>
                </div>
                <div>
                    <p>Eat</p>
                    <img src={require("./components/assets/meal 1.png").default} alt="detail-icon"/>
                    <h3>Included as ltinerary</h3>
                </div>
                <div>
                    <p>Duration</p>
                    <img src={require("./components/assets/time 1.png").default} alt="detail-icon"/>
                    <h3>6 Day 4 Night</h3>
                </div>
                <div>
                    <p>Date Trip</p>
                    <img src={require("./components/assets/calendar 1.png").default} alt="detail-icon"/>
                    <h3>26 August 2020</h3>
                </div>
            </div>
            </div>
            <div id="descriptionn">
            <h2>Description</h2>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.  It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>
            </div>
            <div id="tag">
            <label><span>IDR. 12,398,000</span> / Person</label>
            <div id="sum">
            <button onClick={handleMin}>-</button> {number} <button onClick={handlePlus}>+</button>
            </div>
            
            </div>
            <div id="total">
            <h2>Total:</h2>
            <label>IDR.{total}</label>
            </div>
            <button id="btn-book"><Link to ="/payment">Book Now</Link></button>
            <Footer/>
        </div>
    )
}
export default Tour
