import '../navbar.css'
import {Modal, Alert} from 'react-bootstrap'
import React, { useState,useContext, useHistory } from 'react';
import { API, setAuthToken } from "../../config/api";
import { UserContext } from "../../context/userContext";

function Register(props){



    const [showRegister, setShowRegister] = useState(true);
    const handleCloseRegister = () => setShowRegister(false);


    const history = useHistory();

    const [state, dispatch] = useContext(UserContext);
      
    const [message, setMessage] = useState(null);
  
      const [form, setform] = useState({
        isLogin:false,user:{
          name:"",
          email:"",
          password:"",
          phone:"",
        }}
      )
      const handleRegister =(e) =>{
        setform((prevState) => ({
          ...prevState,
          [e.target.name]:e.target.value,
        }))
      }
      
      const handleSubmit =async (e)=>{
        try{
        e.preventDefault();
        console.log(form)
        const config={
          headers:{
            "Content-Type":"application/json"
          }
        }
        const body = JSON.stringify(form)
        const response = await API.post("/",body,config)
        if (response.data.status === "success...") {
          const alert = (
            <Alert variant="success" className="py-1">
              Success
            </Alert>
          );
          setMessage(alert);
        } else {
          const alert = (
            <Alert variant="danger" className="py-1">
              Failed
            </Alert>
          );
          setMessage(alert);
        }
      } catch (error) {
        const alert = (
          <Alert variant="danger" className="py-1">
            Failed
          </Alert>
        );
        setMessage(alert);
        console.log(error);
      }
    };

    return(
<div id="modal-register">
    <Modal show={showRegister} onHide={handleCloseRegister}>
        <Modal.Header closeButton>
          <Modal.Title>Register</Modal.Title>
        </Modal.Header>
        <div id="body-register">
        <Modal.Body>
            <form onSubmit={handleSubmit}>
            <label>Full Name</label><br/>
            <input name="name" type='text' onChange={handleRegister}/><br/>
            <label>Email</label><br/>
            <input name="email" type='email' onChange={handleRegister}></input><br/>
            <label>Password</label><br/>
            <input name="password" type='password' onChange={handleRegister}></input><br/>
            <label>Phone</label><br/>
            <input name="phone" type='number' onChange={handleRegister}></input><br/>
            <button id="btn-regis"  type="submit">Register</button>
            </form>
            
        </Modal.Body>
        </div>
        <Modal.Footer>
        <div id="btn-register">
        
        
        </div>
        </Modal.Footer>
    </Modal>
    </div>
    )
}

export default Register
